def surface(m:list)->int:
    print(m)
    return 0

class Game:
    def __init__(self):
        self.m={}
    def load(self,l:list):
        for e in l:
            self.m[tuple(e)]=1
    def surface(self):
        c=0
        for x,y,z in self.m:
            if (x-1,y,z) not in self.m:c+=1
            if (x+1,y,z) not in self.m:c+=1
            if (x,y-1,z) not in self.m:c+=1
            if (x,y+1,z) not in self.m:c+=1
            if (x,y,z-1) not in self.m:c+=1
            if (x,y,z+1) not in self.m:c+=1
        return c

